import React from "react";
//import $ from "jquery";
class Favorites extends React.Component {
  constructor(props) {
    super(props);
   // this.removeFav=this.removeFav.bind(this);
   
    var entries = localStorage.getItem("favMovies");
    var obj = JSON.parse(entries);
    for (var k in obj) {
      if (obj[k] instanceof Object) {
        //  console.log(obj[k]);
      } else {
        //  console.log(obj[k] + "<br>");
      }
    }
   
    this.state = {
      fav: obj,
    };
   
  }
  removeFav=(event)=>{
  console.log('hiiiii');
  console.log(event.target.value);
  let val=event.target.value;
 // console.log(this.state.fav);
  this.state.fav.forEach((e) => {
    console.log(e)
  })
 }
  componentDidMount() {
    console.log(this.state.fav);
  }
  render() {
    const favList=this.state.fav;
    console.log(favList);
    if(favList==null){
      return(
        <div class="row searchResults">
          <h5>empty</h5>
        </div>
      )
    }else{
    return (
      <div class="row searchResults">
        {favList.map(function (list,i) {
          return (
            <div class="col-md-4" key={i}>
              <div>
                <img src={list.obj.Poster} alt={list.obj.Title}></img>
              </div>
              <div class="details">
                <p>Title: {list.obj.Title}</p>
                <p>Release Date: {list.obj.Year}</p>
              </div>
              <div>
                <button onClick={this.removeFav}
                  id="fav"
                  class="favBtn"
                  value={list.obj.imdbID}
                  
                >
                  Unlike
                </button>
              </div>
            </div>
          );
        }.bind(this))}
      </div>
    );
      }
  }
}
export default Favorites;
