import React from 'react';
import CardList, { myFavoriteMovies } from "./cardList";
class Favorites extends React.Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  componentDidMount() {
    if (myFavoriteMovies.length > 0) {
      var movieRows = [];
      for (var i = myFavoriteMovies.length - 1; i >= 0; i--) {
        fetch(myFavoriteMovies[i])
          .then(res => res.json())
          .then(data => {
            data.poster_src =
              "https://image.tmdb.org/t/p/w185" + data.poster_path;
            const movieRow = (
              <MovieRow
                key={data.id}
                movie={data}
                posterSrc={data.poster_src}
                favornot
              />
            );
            movieRows.push(movieRow);
            this.setState({ rows: movieRows });
          });
      }
    }
  }

  render() {
    return (
      <div className="Favorite">
        {this.state.rows ? (
          this.state.rows
        ) : (
          <h1>
            {
              "You can view your favorite movies here. If you open new session you're favorite movies will delete."
            }
          </h1>
        )}
      </div>
    );
  }
}
export default Favorites;
