import React from "react";
//import $ from "jquery";
class Favorites extends React.Component {
  constructor(props) {
    super(props);
    // this.removeFav=this.removeFav.bind(this);

    this.entries = localStorage.getItem("favMovies");
    var obj = JSON.parse(this.entries);

    this.state = {
      fav: obj,
      liked:true
    };
  }
  removeFav = (event) => {
    console.log("hiiiii");
    console.log(event.currentTarget.value);
    var val = event.target.value;
    this.setState({liked:false})
    console.log(this.entries);
    var f = JSON.parse(this.entries).filter((i) => i.keyId != val);
    console.log(f);
    localStorage.clear();
    localStorage.setItem("favMovies", JSON.stringify(f));
  };

  render() {
    const favList = this.state.fav;
    const favText=this.state.liked;
    let text;
    if(favText==true){
      text= <span>Liked</span>
    } else{
      text= <span>Unliked</span>
    }
    console.log(favList);
    if (favList == null || favList == "") {
      return (
        <div class="row searchResults">
          <h5>empty</h5>
        </div>
      );
    } else {
      return (
        <div class="row searchResults">
          {favList.map(
            function (list, i) {
              return (
                <div class="col-md-4" key={i}>
                  <div>
                    <img src={list.obj.Poster} alt={list.obj.Title}></img>
                  </div>
                  <div class="details">
                    <p>Title: {list.obj.Title}</p>
                    <p>Release Date: {list.obj.Year}</p>
                  </div>
                  <div>
                    <button
                      onClick={this.removeFav}
                      id="fav"
                      class="favBtn"
                      value={list.keyId}
                    >
                      {text}
                    </button>
                  </div>
                </div>
              );
            }.bind(this)
          )}
        </div>
      );
    }
  }
}
export default Favorites;
