import React from 'react';
class Favorites extends React.Component {
  constructor(props) {
    super(props);
    
    var fav = JSON.parse(localStorage.getItem('favMovies'));
    var entries =Object.keys(fav)
   // var entries = JSON.stringify(fav);
    this.state = {
      fav: []
    };
  }

  componentDidMount() {
  //  const entries = Object.entries();
  //  var entries = JSON.stringify(this.state.fav);
  //var fav = JSON.parse(localStorage.getItem('favMovies'));
    var entries =Object.keys(localStorage.getItem('favMovies'))
    console.log(entries)
    this.setState({
      fav: entries
    });
    console.log(this.state.fav);
  }

  render() {
    return (
      <div class="row searchResults">
        {
                this.state.fav.map(function(e,i) {
                  return(
                <div class="col-md-4" key={i}>
                    <div>
                        <img src={e.Poster} alt={e.Title}></img>
                    </div>
                    <div class="details">
                        <p>Title: {e.Title}</p>
                        <p>Release Date: {e.Year}</p>
                    </div>
                    <div>
                   <button id="fav" class="favBtn" value={e.imdbID} onClick={((e) => this.removeFavorite(e, i))}>Favorite</button>
                    </div>
                </div>
                 )
                })
            }
      </div>
    );
  }
}
export default Favorites;
