import React from "react";
//import $ from "jquery";
class Favorites extends React.Component {
  constructor(props) {
    super(props);
    // var entries = localStorage.getItem('favMovies');
    var entries = localStorage.getItem("favMovies");
    var obj = JSON.parse(entries);
    for (var k in obj) {
      if (obj[k] instanceof Object) {
        //  console.log(obj[k]);
      } else {
        //  console.log(obj[k] + "<br>");
      }
    }
   
    this.state = {
      fav: obj,
    };
    this.removefavourite=this.removefav.bind(this);
  }
 removefavourite(){
  console.log('hiiiii');
 // console.log(data);
 }
  componentDidMount() {
    console.log(this.state.fav);
  }
  render() {
    const favList=this.state.fav;
    console.log(favList);
    if(favList==null){
      return(
        <div class="row searchResults">
          <h5>empty</h5>
        </div>
      )
    }else{
    return (
      <div class="row searchResults">
        {favList.map(function (list,i) {
          return (
            <div class="col-md-4" key={i}>
              <div>
                <img src={list.obj.Poster} alt={list.obj.Title}></img>
              </div>
              <div class="details">
                <p>Title: {list.obj.Title}</p>
                <p>Release Date: {list.obj.Year}</p>
              </div>
              <div>
                <button
                  id="fav"
                  class="favBtn"
                  value={list.obj.imdbID}
                  onClick={this.removefav}
                >
                  Unlike
                </button>
              </div>
            </div>
          );
        })}
      </div>
    );
      }
  }
}
export default Favorites;
