import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
class CardList extends Component {
    
  constructor(props) {
    super(props);
    this.favoriteCheck = this.favoriteCheck.bind(this);
   
  }
    favoriteCheck(title) {
        const uri="http://www.omdbapi.com/";
        const apikeyVal ="a1dac811";
        var favoriteMovies=[];
        axios.get(uri+'?s='+title+'&'+'apikey='+apikeyVal)
        .then(res => {
             favoriteMovies.push(res.data.Search),
            console.log(favoriteMovies)
        })
        .catch(function (error) {
            console.log(error);
           // this.setState({empty:true });
          });
    }
  render() {
    return (
        
                <div class="col-md-4" key={this.props.obj.i}>
                    <div>
                        <img src={this.props.obj.Poster} alt={this.props.obj.Title}></img>
                    </div>
                    <div class="details">
                        <p>Title: {this.props.obj.Title}</p>
                        <p>Release Date: {this.props.obj.Year}</p>
                    </div>
                    <div>
                        <input type="checkbox" class="checkbox" id="checkbox"  onChange={this.favoriteCheck(this.props.obj.Title)}/><label for="checkbox">Favorite</label>
                    </div>
                </div>
              
          
    );
  }
}

export default CardList;
