import React from 'react';
class Favorites extends React.Component {
  constructor(props) {
    super(props);
    
    
    this.state = {
      fav: []
    };
  }

  componentDidMount() {
    let favMovies = JSON.parse(localStorage.getItem('favMovies'));
    this.setState({fav: favMovies});
    console.log(this.state.fav);
  }

  render() {
    return (
      <div className="Favorite">
        {this.state.rows ? (
          this.state.rows
        ) : (
          <h1>
            {
              "You can view your favorite movies here. If you open new session you're favorite movies will delete."
            }
          </h1>
        )}
      </div>
    );
  }
}
export default Favorites;
