import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import $ from'jquery';
class CardList extends Component {
 
  constructor(props) {
    super(props);
    this.state = {
      liked: false,
     // favMovies:[]
    };

    this.addFavorite = this.addFavorite.bind(this);
  }

  
  addFavorite(e,data) {
    console.log(e.target.value, data);
    this.setState({
      liked: true
    });
    localStorage.setItem('favMovies', JSON.stringify(data));
  }

  
   
  render() {
   /* var isFaved = this.state.liked;
    let favBtn;
    if (isFaved) {
      favBtn = <IsNotFav onClick={this.deleteFavorite} />;
    } else {
      favBtn = <IsFav onClick={this.addFavorite} />;
    }*/
    return (
        
                <div class="col-md-4" key={this.props.obj.i}>
                    <div>
                        <img src={this.props.obj.Poster} alt={this.props.obj.Title}></img>
                    </div>
                    <div class="details">
                        <p>Title: {this.props.obj.Title}</p>
                        <p>Release Date: {this.props.obj.Year}</p>
                    </div>
                    <div>
                   <button id="fav" class="favBtn" value={this.props.obj.imdbID} onClick={((e) => this.addFavorite(e, this.props))}>Favorite</button>
                    </div>
                </div>
              
          
    );
  }
}

export default CardList;
