import React from "react";
//import $ from "jquery";
class Favorites extends React.Component {
  constructor(props) {
    super(props);
   // this.removeFav=this.removeFav.bind(this);
   
     this.entries = localStorage.getItem("favMovies");
    var obj = JSON.parse(this.entries);
   
   
    this.state = {
      fav: obj,
    };
   
  }
  removeFav=(event)=>{
  console.log('hiiiii');
  console.log(event.target.value);
  var val=event.target.value;
  var arr=[];
  console.log(this.entries);
 var f=JSON.parse(this.entries).filter(i=>i.keyId!=val)
 console.log(f);
 localStorage.clear();
 localStorage.setItem('favMovies',JSON.stringify(f));
 this.componentDidUpdate();

  
 }
  componentDidUpdate() {
    this.entries = localStorage.getItem("favMovies");
  }
  render() {
    const favList=this.state.fav;
    console.log(favList);
    if(favList==null||favList==''){
      return(
        <div class="row searchResults">
          <h5>empty</h5>
        </div>
      )
    }else{
    return (
      <div class="row searchResults">
        {favList.map(function (list,i) {
          return (
            <div class="col-md-4" key={i}>
              <div>
                <img src={list.obj.Poster} alt={list.obj.Title}></img>
              </div>
              <div class="details">
                <p>Title: {list.obj.Title}</p>
                <p>Release Date: {list.obj.Year}</p>
              </div>
              <div>
                <button onClick={this.removeFav}
                  id="fav"
                  class="favBtn"
                  value={list.keyId}
                  
                >
                  Unlike
                </button>
              </div>
            </div>
          );
        }.bind(this))}
      </div>
    );
      }
  }
}
export default Favorites;
