import React from 'react';
import axios from 'axios';
class Home extends React.Component {
    constructor(props) {
        //var movieData=[];
        super(props);
        this.onChangeSearchText = this.onChangeSearchText.bind(this);
        this.onChangeSearchType = this.onChangeSearchType.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
  
            this.state = {
                searchText: '',
                searchType: '',
                movieData: []
            }
        }
        
        onChangeSearchText(e) {
            this.setState({
                searchText: e.target.value
            });
          }
          onChangeSearchType(e) {
            this.setState({
                searchType: e.target.value
            })  
          }
          onSubmit(e) {
            e.preventDefault();
           
            const uri="http://www.omdbapi.com/";
            const apikeyVal ="a1dac811";
            const obj = {
                t: this.state.searchText,
                type: this.state.searchText,
                apikey : apikeyVal
              };
            console.log(`The values are ${this.state.searchText}, ${this.state.searchType}`)
            axios.get(uri+'?s='+obj.t+'&'+'apikey='+obj.apikey)
                .then(res => {
                    this.setState({ movieData: res.data });
                    console.log(this.state.movieData)
                })
                .catch(function (error) {
                    console.log(error);
                  });
                 // console.log(this.state.movieData);
            this.setState({
                searchText: '',
                searchType: '',
             })
          }
          renderList(){
            return this.props.movieData.map((element, index) =>{
                 
                    <div class="col-md-4" >
                     <img src="[element.poster]" alt="img"></img>
                     <div class="details" key={index}>
                         <p>Title: {element.title}</p>
                         <p>Release Date: {element.Released}</p>
                         <input type="checkbox" class="checkbox" id="checkbox" value={element.imdbID} />
                  <label for="checkbox"> <svg id="heart-svg" viewBox="467 392 58 57" xmlns="http://www.w3.org/2000/svg">
                          <g id="Group" fill="none" fill-rule="evenodd" transform="translate(467 392)">
                              <path d="M29.144 20.773c-.063-.13-4.227-8.67-11.44-2.59C7.63 28.795 28.94 43.256 29.143 43.394c.204-.138 21.513-14.6 11.44-25.213-7.214-6.08-11.377 2.46-11.44 2.59z" id="heart" fill="#AAB8C2" />
                              <circle id="main-circ" fill="#E2264D" opacity="0" cx="29.5" cy="29.5" r="1.5" />
                              <g id="heartgroup7" opacity="0" transform="translate(7 6)">
                                  <circle id="heart1" fill="#9CD8C3" cx="2" cy="6" r="2" />
                                  <circle id="heart2" fill="#8CE8C3" cx="5" cy="2" r="2" />
                              </g>
                              <g id="heartgroup6" opacity="0" transform="translate(0 28)">
                                  <circle id="heart1" fill="#CC8EF5" cx="2" cy="7" r="2" />
                                  <circle id="heart2" fill="#91D2FA" cx="3" cy="2" r="2" />
                              </g>
                              <g id="heartgroup3" opacity="0" transform="translate(52 28)">
                                  <circle id="heart2" fill="#9CD8C3" cx="2" cy="7" r="2" />
                                  <circle id="heart1" fill="#8CE8C3" cx="4" cy="2" r="2" />
                              </g>
                              <g id="heartgroup2" opacity="0" transform="translate(44 6)">
                                  <circle id="heart2" fill="#CC8EF5" cx="5" cy="6" r="2" />
                                  <circle id="heart1" fill="#CC8EF5" cx="2" cy="2" r="2" />
                              </g>
                              <g id="heartgroup5" opacity="0" transform="translate(14 50)">
                                  <circle id="heart1" fill="#91D2FA" cx="6" cy="5" r="2" />
                                  <circle id="heart2" fill="#91D2FA" cx="2" cy="2" r="2" />
                              </g>
                              <g id="heartgroup4" opacity="0" transform="translate(35 50)">
                                  <circle id="heart1" fill="#F48EA7" cx="6" cy="5" r="2" />
                                  <circle id="heart2" fill="#F48EA7" cx="2" cy="2" r="2" />
                              </g>
                              <g id="heartgroup1" opacity="0" transform="translate(24)">
                                  <circle id="heart1" fill="#9FC7FA" cx="2.5" cy="3" r="2" />
                                  <circle id="heart2" fill="#9FC7FA" cx="7.5" cy="2" r="2" />
                              </g>
                          </g>
                      </svg> </label>
                     </div>
                 </div>
                
            })
          }
  render() {
   
    return (
        <div className="container">
        <div class="row searchForm">
        <div class="col-md-6 offset-md-3">
            <form class="form-inline " onSubmit={this.onSubmit}>
                <div class="center_form">
                <input type="text" class="form-control" value={this.state.searchText}
                        onChange={this.onChangeSearchText} id="seachBox" placeholder="search"/>
                    <select class="form-control" value={this.state.searchType}
                        onChange={this.onChangeSearchType} id="searchType">
                        <option value="" selected disabled>select</option>
                        <option value="all">All</option>
                        <option value="movies">Movies</option>
                        <option value="episodes">Episodes</option>
                        <option value="series">Series</option>
                    </select>
                    <button type="submit" class="btn btn-primary">Sign in</button>
                </div>
            </form>
        </div>
    </div>
    <div class="row searchResults">
        {this.renderList()}
   
</div>
</div>
    );
  }
}
export default Home;
