import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import $ from'jquery';
class CardList extends Component {
  myFavoriteMovies = [];
  constructor(props) {
    super(props);
    this.state = {
      liked: false,
      favMovies:[]
    };
    this.addFavorite = this.addFavorite.bind(this);
    this.deleteFavorite = this.deleteFavorite.bind(this);
    this.handleClick = this.handleClick.bind(this);
    function IsFav(props) {
      return <button onClick={props.onClick} className="heart" />;
    }
    
    function IsNotFav(props) {
      return <button onClick={props.onClick} className="toggled heart" />;
    }
  }
  
  addFavorite() {
    this.setState({ liked: true });
    var myFavoriteMovies = [];
    const uri="http://www.omdbapi.com/";
    const apikeyVal ="a1dac811";
    const favMovie = "".concat(
      uri,
      "movie/",
      this.props.obj.imdbID,
      "?api_key=",
      apikeyVal
    );
    myFavoriteMovies.push(favMovie);
  }

  deleteFavorite() {
    this.setState({ liked: false });
  }
  handleClick() {
    this.setState({
      liked: true,
     
    });
    console.log(this.state.liked);
    console.log(this.state.favMovies);
  }
   
  render() {
    var isFaved = this.state.liked;
    let favBtn;
    if (isFaved) {
      favBtn = <IsNotFav onClick={this.deleteFavorite} />;
    } else {
      favBtn = <IsFav onClick={this.addFavorite} />;
    }
    return (
        
                <div class="col-md-4" key={this.props.obj.i}>
                    <div>
                        <img src={this.props.obj.Poster} alt={this.props.obj.Title}></img>
                    </div>
                    <div class="details">
                        <p>Title: {this.props.obj.Title}</p>
                        <p>Release Date: {this.props.obj.Year}</p>
                    </div>
                    <div>
                   <button id="fav" >Favorite</button>
                    </div>
                </div>
              
          
    );
  }
}

export default CardList;
