  constructor(props) {
    super(props);
    this.favoriteCheck = this.favoriteCheck.bind(this);
   
  }
    favoriteCheck(e) {
      var $this=$(e.currentTarget);
      var description=$this.attr('data')
        const uri="http://www.omdbapi.com/";
