        } else if($this.prop('checked')==false){
         // favoriteMovies.splice(res.data.Search);
        }
    }
  render() {
    return (
        
                <div class="col-md-4" key={this.props.obj.i}>
                    <div>
                        <img src={this.props.obj.Poster} alt={this.props.obj.Title}></img>
                    </div>
                    <div class="details">
                        <p>Title: {this.props.obj.Title}</p>
                        <p>Release Date: {this.props.obj.Year}</p>
                    </div>
                    <div>
                        <input type="checkbox" class="checkbox" id="checkbox" data={this.props.obj} onClick={this.favoriteCheck(e)}/><label for="checkbox">Favorite</label>
                    </div>
                </div>
              
          
    );
  }
}

export default CardList;
