import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import $ from'jquery';
class CardList extends Component {
    
  constructor(props) {
    super(props);
    this.state = {
      liked: false,
      favMovies:[]
    };
    this.handleClick = this.handleClick.bind(this);
  
  }
  handleClick(valMovie) {
    this.setState({
      liked: !this.state.liked,
      favMovies: valMovie
    });
    console.log(this.state.liked);
    console.log(this.state.favMovies);
  }
   
  render() {
    const text = this.state.liked ? 'liked' : 'haven\'t liked';
    const label = this.state.liked ? 'Unlike' : 'Like'
    return (
        
                <div class="col-md-4" key={this.props.obj.i}>
                    <div>
                        <img src={this.props.obj.Poster} alt={this.props.obj.Title}></img>
                    </div>
                    <div class="details">
                        <p>Title: {this.props.obj.Title}</p>
                        <p>Release Date: {this.props.obj.Year}</p>
                    </div>
                    <div>
                    <button className="btn btn-primary" onClick={this.handleClick(this.props.obj.i)}>
          {label}</button>
                    </div>
                </div>
              
          
    );
  }
}

export default CardList;
